import{_ as t,c as l,o as n,j as e,a as r}from"./chunks/framework.Cc4yYURl.js";const f=JSON.parse('{"title":"两个和尚的故事","description":"","frontmatter":{"createDate":"2021/02/26","title":"两个和尚的故事"},"headers":[],"relativePath":"生活咏叹调/两个和尚的故事/index.md","filePath":"生活咏叹调/两个和尚的故事/index.md","lastUpdated":1756918447000}'),s={name:"生活咏叹调/两个和尚的故事/index.md"};function d(o,a,i,p,c,u){return n(),l("div",null,a[0]||(a[0]=[e("h1",{id:"两个和尚的故事",tabindex:"-1"},[r("两个和尚的故事 "),e("a",{class:"header-anchor",href:"#两个和尚的故事","aria-label":'Permalink to "两个和尚的故事"'},"​")],-1),e("p",null,"2021年02月26日",-1),e("p",null,"两个和尚住在隔壁，所谓隔壁就是隔壁那座山，他们分别住在相邻的两座山上的庙里。两座山之间有一条溪，这两个和尚每天都会在同一时间下山去溪边挑水，久而久之，他们便成为了好朋友。",-1),e("p",null,"就这样，时间在每天挑水中不知不觉已经过了 5 年。突然有一天，左边这座山的和尚没有下山挑水，右边那座山的和尚心想：“他大概睡过头了。”便不以为意。",-1),e("p",null,"哪知道第二天左边这座山的和尚还是没有下山挑水，第三天也一样。过了一个星期还是一样，直到过了一个月，右边那座山的和尚终于忍不住了，他心想：“我的朋友可能生病了，我要过去拜访他，看看能帮上什么忙。”",-1),e("p",null,"于是他便爬上了左边这座山，去探望他的老朋友。",-1),e("p",null,"等他到了左边这座山的庙里，看到他的老友之后大吃一惊，因为他的老友正在庙前打太极拳，一点也不像一个月没喝水的人。他很好奇地问：“你已经一个月没有下山挑水了，难道你可以不用喝水吗？”左边这座山的和尚说：“来来来，我带你去看。”于是他带着右边那座山的和尚走到庙的后院，指着一口井说： “这 5 年来，我每天做完功课后都会抽空挖这口井，即使有时很忙，能挖多少就算多少。如今终于让我挖出井水，我就不用再下山挑水，我可以有更多时间练我喜欢的太极拳。”",-1),e("hr",null,null,-1),e("p",null,"上面一个故事告诉我们，如果你挖了一口井，千万要记得告诉你的朋友，否则人家会担心的。 😊",-1)]))}const x=t(s,[["render",d]]);export{f as __pageData,x as default};
